import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import "../styles/overload.css";
import "../styles/helpers.css";
import "../styles/index.css";
import '../../node_modules/font-awesome/css/font-awesome.min.css';
var ip = require("../server.json").ip;

function InvalidLink(props) {
    return <h1>This Link is invalid</h1>
}

function InputBoxes(props) {
    return <h1>Valid link</h1>
}

function DiplayStatus(props){
    if (props.validKey) {
        return <InputBoxes />
    } else {
        return <InvalidLink />
    }
}

    // if (props.status === 'not found' || props.status === 'something went wrong') {
    //     return <h1> Error! invalid link. </h1>
    // }
    // if (props.status === 'already used') {
    //     return <h1> Error! link already used. </h1>
    // }
    // if (props.status === 'activated') {
    //     return <h1> Success! your account has been activated. </h1>
    // }

export default class ResetPass extends Component {
    constructor(props) {
        super(props);

        this.state = {
			email: '',
            validKey: false
        }
    }

    componentDidMount() {
        const { vkey } = this.props.match.params;

		axios.get(`${ip}/users/verifyKey/${vkey}`).then(res => {
            this.setState({ validKey: res.data.validKey, email: res.data.email });
            console.log(this.state.validKey);
            console.log(this.state.email);
        }).catch(() => {
			this.setState({ validKey: false });
		}); 
    }

    render() {
        return(
            <section className="section hero">
            <nav className="navbar hero-head">
                <div className="container">
                    <div className="navbar-brand">
                        <figure className="navbar-item image">
                            <img src={require('../images/logo.png')} className="logo_use" alt="Why is this logo broken"/>
                        </figure>
                        <span className="navbar-burger burger" data-target="navMenu">
                            <span></span>
                            <span></span>
                            <span></span>
                        </span>
                    </div>
                </div>
            </nav>
                <div className="container">
                    <div className="columns is-centered shadow">
                        <div className="column is-half bg_white_1">
                            <div className="column center has-text-centered">
                                    <hr></hr>
                                    <DiplayStatus validKey={this.state.validKey}/>
                                    <br></br>
                                    <br></br>
                                    <hr></hr>
                            </div>

                        </div>
                    </div>
                </div>
            </section>
        )
    }
}
