        }
        //console.log(jwt);
    }

    render() {
        if (this.state.user === undefined) {
            return (
                <div><h1>Loading...</h1></div>
            );
        }
        return (
        <div>
            {this.props.children}
        </div>
        )
    }
}

export default withRouter(Authenticated);
