import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom';
import "../styles/overload.css";
import "../styles/helpers.css";
import "../styles/index.css";
import '../../node_modules/font-awesome/css/font-awesome.min.css';
var ip = require("../server.json").ip;

function PasswordInput(props){
    if (props.validKey) {
        return (
            <div>
                <div className="field">
                    <label className="label">Email</label>
                    <div className="control has-icons-left has-icons-right">
                        <input className="input" type="email" placeholder="Email input" value={props.email} onChange={props.onChange} required />
                        <span className="icon is-small is-left">
                            <i className="fa fa-envelope"></i>
                        </span>
                        <span className="icon is-small is-right">
                            <i className="fa fa-exclamation-triangle"></i>
                        </span>
                    </div>
                    <p className="help is-danger"></p>
                </div>
                <br></br>
                <br></br>
                <div className="control">
                    <button className="button is-warning is-rounded" onClick={props.onClick}>Send</button>
                </div>
            </div>
        )
    } else {
        return <h1>This Link is invalid</h1>
    }
    return <h1>Loading...</h1>
}

export default class ResetPass extends Component {
    constructor(props) {
        super(props);
        this.onChangeEmail = this.onChangeEmail.bind(this);
		this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            email: '',
            password: '',
            validKey: false
        }
    }

    componentDidMount() {
        const { vkey } = this.props.match.params;

		axios.get(`${ip}/users/verifyKey/${vkey}`).then(res => {
            this.setState({ validKey: res.data.validKey, email: res.data.email });
        }).catch(() => {
			this.setState({ validKey: false });
		}); 
    }

    onChangePassword(e) {
        this.setState({email: e.target.value});
    }

    onSubmit(e) {
        e.preventDefault();
        console.log(this.state.email);
    }

    render() {
        return(
            <section className="section hero">
            <nav className="navbar hero-head">
                <div className="container">
                    <div className="navbar-brand">
                        <figure className="navbar-item image">
                            <img src={require('../images/logo.png')} className="logo_use" alt="Why is this logo broken"/>
                        </figure>
                        <span className="navbar-burger burger" data-target="navMenu">
                            <span></span>
                            <span></span>
                            <span></span>
                        </span>
                    </div>
                </div>
            </nav>
                <div className="container">
                    <div className="columns is-centered shadow">
                        <div className="column is-half bg_white_1">
                            <div className="column center has-text-centered">
                                <hr></hr>
                                <PasswordInput validKey={this.state.validKey} email={this.state.email} onChange={this.onChangePassword} onClick={this.onSubmit}/>
                                <br></br>
                                <br></br>
                                <hr></hr>
                            </div>

                        </div>
                    </div>
                </div>
            </section>
        )
    }
}
